import { NextApiRequest, NextApiResponse } from 'next'
import jsonResult from '../../shared/functions/convertDatasetToJson'

import { Centroid } from '../../shared/class/Centroid'
import pearson from '../../shared/functions/pearson'

const { range, map, pipe, flip, forEach: forE, all: every } = require('ramda')
const { reduce, forEach } = require('list/curried')

const random = (from: number) => (to: number) =>
  from + Math.random() * (to - from)

const assignToCentroid = (wordCount) => (blogs) => (centroids) => {}

const kMean = (req: NextApiRequest, res: NextApiResponse) => {
  const CLUSTERS = 5
  const MAX_ITERATIONS = 50
  let k = random(1)(20)
  const n = 706

  const centroids = []

  for (let i = 0; i < k; i++) {
    centroids[i] = new Centroid()
    jsonResult().forEach(({ min, max }, idx) => {
      centroids[i].setTotalWords(idx, random(min)(max))
    })
  }

  res.json(centroids)
}

export default kMean
