/* Lib */
import cx from 'classnames'
import { useEffect, useState } from 'react'
import { Container, Accordion, Card, Button, ListGroup } from 'react-bootstrap'
import { useForm } from 'react-hook-form'

const Index = (): any => {
  const { register, handleSubmit, errors } = useForm()
  const [result, setResult] = useState([])

  const onSubmit = (data: any) => fetchSearch(data.query)

  const fetchSearch = async (q: string) => {
    const body: object = {
      query: q
    }
    await fetch('/api/search', body)
      .then((res) => {
        res.json()
      })
      .then((result) => {
        setResult(result)
      })
  }

  return (
    <>
      <Container className={cx(['mt-5'])}>
        <form className="form-inline" onSubmit={handleSubmit(onSubmit)}>
          {result && console.log(result)}
          {/* register your input into the hook by invoking the "register" function */}
          <div className="form-group mx-sm-3 mb-2">
            <input className="form-control" name="query" ref={register} />
          </div>
          <input className="btn btn-primary mb-2" type="submit" />
        </form>
      </Container>
    </>
  )
}

export default Index
