/* Centroid functions */
const Centroid = () => {
  // this.totalWords = {}
  this.assignments = []
  this.prevAssignments
}

/** Decide total words */
// Centroid.prototype.setTotalWords = (id, count) => {
//   this.totalWords[id] = count
// }

/** Assign */
Centroid.prototype.assign = (blog) => {
  this.assignments.push(blog)
}

/** Clear assignments */
Centroid.prototype.clearAssignments = () => {
  this.prevAssignments = this.assignments
  this.assignments = []
}

Centroid.prototype.isUnchanged = () => {
  return this.assignments == this.prevAssignments
}

module.exports = {
  Centroid,
  /** assign :: Centriod -> Blog -> () */
  assign: (centroid) => (blog) => centroid.assign(blog),
  /** clearAssignments :: Centriod -> () */
  clearAssignments: (centroid) => centroid.clearAssignments(),
  /** isUnchanged :: Centriod -> Boolean */
  isUnchanged: (centroid) => centroid.isUnchanged(),
}
