import { NextApiRequest, NextApiResponse } from 'next'
import jsonResult from '../../shared/functions/convertDatasetToJson'

import {
  Centroid,
  clearAssignments,
  assign,
  isUnchanged,
} from '../../shared/functions/centroid'

const random = (from) => (to) => from + Math.random() * (to - from)

const kMean = (req: NextApiRequest, res: NextApiResponse) => {
  const n = jsonResult()[0].total
  const centroids = []

  for (let i = 0; i < 5; i++) {
    centroids[i] = new Centroid()
    for (let j = 0; i <= n; j++) {
      centroids[i].setTotalWords(i, random(0)(5))
    }
  }

  res.json(random(0)(5))
}

export default kMean
