    centroids.forEach((e) => e.clearAssignments())

    for (let b in jsonResult()) {
      let distance = Infinity
      let closest // best

      for (let c in centroids) {
        const cDist = pearson(b, c)
        //   if (cDist < distance) {
        //     closest = c
        //     distance = cDist
        //   }
      }

      // centroids[i].assign(b)
    }
  }

  res.json(JSON.stringify(jsonResult()[0].blogs, null, 2))
}

export default kMean
