import { Console } from 'console';
import { NextApiRequest, NextApiResponse } from 'next';
const sqlite3 = require('sqlite3');
const sqlite = require('sqlite');

const weightedCalculations = async (a: Array<any>) => {
    
    let arr: Array<any> = []; // array for concat

    /* wsum */
    for (let i: number = 0; i < a.length; i++) {
        for (let j: number = 0; j < a.length; j++) {
            arr.push(a[i][j]);
        }
    }

    let result = arr.reduce((accumulator, cur) => {

        let id = cur.movieId;
        
        let found: any = accumulator.find((elem: { movieId: any; }) => {
            return elem.movieId == id;
        })

        if (found) {
            found.wScore += cur.wScore;
        } else {
            accumulator.push(cur)
        }
        
        return accumulator;

    }, []);

    result.sort((a: { wScore: any; }, b: { wScore: any; }) =>
        parseFloat(b.wScore) - parseFloat(a.wScore)
    ); // sort movieId ascending order

    return arr;
}

/**
 * Calculating weighted score
 * @param a 
 */
const weightedScore = async (a: any, score: number) => {
    
    let arr: Array<any> = [];

    for (let i = 0; i < a.length; i++) {
        let wScore: number = a[i]['rating'] * score;

        arr.push({
            movieId: a[i]['movieId'],
            title: a[i]['title'],
            wScore: wScore,
            similarity_score: parseInt(score)
        });

    }

    return arr;
}
 
/**
 * Calculating euclidean score
 * @param a 
 * @param b 
 */
const euclideanUser = async (a: any, b: any) => {
    let sim: number = 0; // integer
    let n: number = 0; //  counter for number of matching products

    for (let i = 0; i < a.length; i++) {
        for (let j = 0; j < b.length; j++) {
            if (a[i]['movieId'] == b[j]['movieId']) {
                sim += (a[i]['rating'] - b[j]['rating']) ** 2;
                n += 1;
            }
        }
    }

    if (n == 0) {
        return 0;
    }

    let inv: number = 1 / (1 + sim);

    return (Math.round(inv * 100) / 100).toFixed(2);
    // return inv;
};

const euclideanAPI = async (req: NextApiRequest, res: NextApiResponse) => {
    const id: any = req.query.id; // main user

    /* DB connection */
    const db = await sqlite.open({
        filename: './mydb.sqlite',
        driver: sqlite3.Database
    });
   
    /* sql */
    let user: any = await db.all('SELECT * FROM users where id = ?', [id]);
    /* Retrieve ratings that belongs to main user */
    let userRatings: any = await db.all('SELECT userId, movies.title, movieId, ratings.rating FROM ratings, users, movies WHERE users.id = userId AND movies.id = movieId AND users.id = ?', [id]);
    let otherUsers: any = await db.all('SELECT name, id FROM users where id != ?', [id]);

    let userSimilarity: Array<any> = []; // adding euclidean result here
    let x: Array<any> = []; // adding euclidean result here

    /* Getting the similarity between users */
    for (let i: number = 0; i < otherUsers.length; i++) {

        let id: number = otherUsers[i]['id'];
        let name: String = otherUsers[i]['name'];

        /* Retrieve ratings for all the other users */
        let otherUserRatings: any = await db.all('SELECT ratings.movieId, title, ratings.rating FROM ratings, users, movies WHERE users.id = userId AND movies.id = movieId AND users.id = ?', [id]);
     
        let euclideanScore: any = await euclideanUser(userRatings, otherUserRatings); // get euclidean score

        let wScore: any = await weightedScore(otherUserRatings, euclideanScore); // get weighter score

        x.push({weighted_scores: wScore});

        /* Push objects */
        userSimilarity.push({
            id: id,
            user_name: name,
            score: euclideanScore,
            weighted_scores: wScore // high score = more similar
        });
    }

    /* Push array with user objects */
    user[0]['user_similarity'] = userSimilarity;

    // user = JSON.stringify(user, null, 2);

    x = user.map(y => {
        return y['user_similarity'].map(k => {
            return k['weighted_scores'];
        });
    })

    let test2 = await weightedCalculations(x[0]);

    // let test = JSON.stringify(x[0], null, 2);
    let op = JSON.stringify(test2, null, 2);

    res.json(op);
}

export default euclideanAPI;
