import { NextApiRequest, NextApiResponse } from 'next';
const sqlite3 = require('sqlite3');
const sqlite = require('sqlite');

const euclidean = async (A, B) => {
    let sim: number = 0; // integer
    let n: number = 0; //  counter for number of matching products

    for (let i = 0; i < A.length; i++) {
        return A[i]['rating']
    }

    // return A;
};

const euclideanAPI = async (req: NextApiRequest, res: NextApiResponse) => {
    const id = req.query.id; // main user

    /* DB connection */
    const db = await sqlite.open({
        filename: './mydb.sqlite',
        driver: sqlite3.Database
    });
   
    /* Retrieve ratings that belongs to main user */
    let ratings1 = await db.all('SELECT userId, movies.title, movieId, ratings.rating FROM ratings, users, movies WHERE users.id = userId AND movies.id = movieId AND users.id = ?', [id]);

    /* Retrieve ratings for all the other users */
    let ratings2 = await db.all('SELECT userId, movies.title, ratings.movieId, ratings.rating FROM ratings, users, movies WHERE users.id = userId AND movies.id = movieId AND users.id = ?', [2]);

    let result = await euclidean(ratings1, ratings2);
    result = JSON.stringify(result, null, 2);

    // res.json(ratings1);
    res.json(result);
}

export default euclideanAPI;
