let csvToJson = require('convert-csv-to-json')

const jsonResult = () => {
  const rawJson = csvToJson.getJsonFromCsv('./shared/data/blogdata.csv')

  /* New Json Array */
  let arr = [
    {
      blogs: [],
    },
  ]

  // for (let i = 0; i < rawJson.length; i++) {
  //   /* Total words per blog */
  //   arr[0]['blogs'].push({
  //     name: rawJson[i]['Blog'],
  //   })
  // }

  for (var propName in rawJson[0]) {
    if (rawJson.hasOwnProperty(propName)) {
      arr[0]['blogs'].push({
        totalWords: rawJson[propName],
      })
    }
  }

  return arr
}

export default jsonResult
