let csvToJson = require('convert-csv-to-json')

const jsonResult = () => {
  const rawJson = csvToJson.getJsonFromCsv('./shared/data/blogdata.csv')

  /* New Json Array */
  let arr = [
    {
      blogs: [],
    },
  ]

  for (let i = 0; i < rawJson.length; i++) {
    /* Total words per blog */
    arr[0]['blogs'].push({
      name: rawJson[i]['Blog'],
    })

    for (let value of rawJson[i]) {
      arr[0]['blogs'].push({
        totalWords: value,
      })
    }
  }

  return arr
}

export default jsonResult
