const sqlite3 = require('sqlite3');
const sqlite = require('sqlite');

const getUserRatings = async (id) => {
    /* DB connection */
    const db = await sqlite.open({
        filename: './mydb.sqlite',
        driver: sqlite3.Database
    });

    return await db.all('SELECT userId, movies.title, movieId, ratings.rating FROM ratings, users, movies WHERE users.id = userId AND movies.id = movieId AND users.id = ?', [id]);
}

export default getUserRatings;
