/* Lib */
import cx from 'classnames';
import { useEffect, useState } from 'react';

const Index = () => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [users, setUsers] = useState([]);
    const [target, setTarget] = useState(1);
    const [sim, setSim] = useState(0);

    useEffect(() => {
        fetch('/api/users')
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setUsers(result);
                    console.log(result);
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
        )
    })

    const handleUserChange = (e: any) => {
        setTarget(e.target.value);
    }

    const handleSimChange = (e: any) => {
        setSim(e.target.value);
    }

    let simList = [{ value: 1, description: 'Euclidean' }, { value: 2, description: 'Pearson Colleration' }];

    return (
        <div className={cx(['container'], ['m-5'])}>
            <div className={cx(['row'])}>
                <div className={cx(['col-sm'])}>
                    <select className="custom-select" onChange={handleUserChange}>
                        {users.map((u: any, i: number) => <option value={u.id} key={i}>{u.id}: {u.name}</option>)}
                    </select>
                </div>
                <div className={cx(['col-sm'])} onChange={handleSimChange}>
                    <select className="custom-select">
                        {simList.map((sim: any, i: number) => <option value={i} key={i}>{sim.description}</option>)}
                    </select>
                </div>
            </div>
        </div>
    )
};

export default Index;
