/* Lib */
import cx from 'classnames';
import { useEffect, useState } from 'react';

/* Components */
import Dropdown from './components/dropdown';

const Index = () => {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetch('/api/users')
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setUsers(result);
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
        )
    })

    let sim = [{ value: 1, description: 'Euclidean' }, { value: 2, description: 'Pearson Colleration' }];

    return (
        <div className={cx(['container'], ['m-5'])}>
            <div className={cx(['row'])}>
                <div className={cx(['col-sm'])}>
                    <select className="custom-select">
                        {users.map((u: any, i: number) => <option value={u.id} key={i}>{u.id}: {u.name}</option>)}
                    </select>
                </div>
                <div className={cx(['col-sm'])}>
                    <select className="custom-select">
                        {sim.map((sim: any, i: number) => <option value={i} key={i}>{sim.description}</option>)}
                    </select>
                </div>
            </div>
        </div>
    )
};

export default Index;
