import { NextApiRequest, NextApiResponse } from 'next';
const sqlite3 = require('sqlite3');
const sqlite = require('sqlite');

const euclidean = (A, B) => {
    let sim: number = 0; // integer
    let n: number = 0; //  counter for number of matching products

    if (n == 0) {
        return 0;
    }
};

const euclideanAPI = async (req: NextApiRequest, res: NextApiResponse) => {
    const id = req.query.id; // main user

    /* DB connection */
    const db = await sqlite.open({
        filename: './mydb.sqlite',
        driver: sqlite3.Database
    });
   
    /* Retrieve ratings that belongs to main user */
    let ratings1 = await db.all('SELECT userId, movieId, ratings.rating FROM ratings, users, movies WHERE users.id = userId AND movies.id = movieId AND users.id = ?', [id]);
    ratings1 = JSON.stringify(ratings1, null, 2);

    /* Retrieve ratings for all the other users */
    let ratings2 = await db.all('SELECT userId, ratings.movieId, ratings.rating FROM ratings, users, movies WHERE users.id = userId AND movies.id = movieId AND users.id != ?', [id]);
    ratings2 = JSON.stringify(ratings2, null, 2);

    // res.json(ratings1);
    res.json(ratings2);
}

export default euclideanAPI;
