export class Centroid {
  wordCount: any = {}
  assignments: any = []
  prevAssignments: any

  setTotalWords(id: number, count: number) {
    this.wordCount[id] = count
  }

  assign(blog) {
    this.assignments.push(blog)
  }

  clearAssignments() {
    this.prevAssignments = this.assignments
    this.assignments = []
  }

  isUnchanged(): boolean {
    return this.assignments == this.prevAssignments
  }
}
